mapper.select:
SELECT `uid`, `method`, `url_pattern`, `created_user_uid`, `created_utc_at`, `updated_user_uid`, `updated_utc_at` FROM rc_manage_resource
AND `method` LIKE CONCAT('%', #{pagination.method, jdbcType=VARCHAR}, '%')
AND `url_pattern` LIKE CONCAT('%', #{pagination.urlPattern}, '%')
AND `created_utc_at` >= #{pagination.startDate}
AND `created_utc_at` <= #{pagination.endDate}
LIMIT #{pagination.limit, jdbcType=INTEGER} OFFSET #{pagination.offset, jdbcType=INTEGER}

SELECT COUNT(*) > 0
FROM `rc_manage_resource`
WHERE `method` = #{method, jdbcType=VARCHAR}
  AND `url_pattern` = #{url_pattern, jdbcType=VARCHAR}
