mapper.select:
SELECT `id`, `type`, `value`, `category`, `order`, `created_user_uid`, `created_utc_at`, `updated_user_uid`, `updated_utc_at` FROM rc_common_env_code
AND `id` LIKE CONCAT('%', #{pagination.id, jdbcType=VARCHAR}, '%')
AND `type` LIKE CONCAT('%', #{pagination.type}, '%')
AND `value` LIKE CONCAT('%', #{pagination.value}, '%')
AND `category` LIKE CONCAT('%', #{pagination.category}, '%')
AND `created_utc_at` >= #{pagination.startDate}
AND `created_utc_at` <= #{pagination.endDate}
ORDER BY `updated_utc_at` DESC LIMIT #{pagination.limit, jdbcType=INTEGER} OFFSET #{pagination.offset, jdbcType=INTEGER}
