mapper.select:
/* SystemResourceManagementMapper.selectList() */ SELECT `uid`, `method`, `url_pattern`, `enabled`, `created_user_uid`, `created_utc_at`, `updated_user_uid`, `updated_utc_at` FROM rc_manage_resource
AND `uid` LIKE CONCAT('%', #{pagination.method, jdbcType=VARCHAR}, '%')
AND `method` LIKE CONCAT('%', #{pagination.method, jdbcType=VARCHAR}, '%')
AND `url_pattern` LIKE CONCAT('%', #{pagination.urlPattern}, '%')
AND `created_utc_at` >= #{pagination.startDate}
AND `created_utc_at` <= #{pagination.endDate}
ORDER BY `updated_utc_at` DESC LIMIT #{pagination.limit, jdbcType=INTEGER} OFFSET #{pagination.offset, jdbcType=INTEGER}

/* SystemResourceManagementMapper.isDuplicateByMethodAndUrlPattern() */
SELECT COUNT(*) > 0
FROM rc_manage_resource
WHERE `method` = #{method, jdbcType=VARCHAR}
  AND `url_pattern` = #{urlPattern, jdbcType=VARCHAR}

/* SystemResourceManagementMapper.isOwner() */
SELECT COUNT(*) > 0
FROM rc_manage_resource
WHERE `uid` = #{uid, jdbcType=BIGINT}
  AND `created_user_uid` = #{userUid, jdbcType=VARCHAR}

mapper.update:
/* SystemResourceManagementMapper.update() */ UPDATE rc_manage_resource
`method` = #{method, jdbcType=VARCHAR},
`url_pattern` = #{urlPattern, jdbcType=VARCHAR},
`description` = #{description, jdbcType=VARCHAR},
`enabled` = #{enabled, jdbcType=VARCHAR}, `updated_user_uid` = #{userUid, jdbcType=VARCHAR}, `updated_utc_at` = CURRENT_TIMESTAMP()
WHERE `uid` = #{uid, jdbcType=BIGINT}

mapper.delete:
/* SystemResourceManagementMapper.delete() */
DELETE
FROM rc_manage_resource
WHERE `uid` = #{uid, jdbcType=BIGINT}

mapper.select:
/* SystemResourceManagementMapper.isExistResourceByUid() */
SELECT COUNT(*) > 0
FROM rc_manage_resource
WHERE `uid` = #{uid, jdbcType=VARCHAR}
