mapper.select:
/* SystemRoleManagementMapper.selectList() */ SELECT `uid` , `name` , `description` , `enabled` , `created_user_uid` , `created_utc_at` , `updated_user_uid` , `updated_utc_at` FROM rc_manage_role
AND `uid` LIKE CONCAT('%', #{condition.uid, jdbcType=VARCHAR}, '%')
AND `name` LIKE CONCAT('%', #{condition.name, jdbcType=VARCHAR}, '%')
AND `created_utc_at` >= #{condition.startDate, jdbcType=TIMESTAMP}
AND `created_utc_at` <= #{condition.endDate, jdbcType=TIMESTAMP}
ORDER BY `updated_utc_at` DESC LIMIT #{pagination.limit, jdbcType=INTEGER} OFFSET #{pagination.offset, jdbcType=INTEGER}

/* SystemRoleManagementMapper.isExistByName() */
SELECT COUNT(*) > 0
FROM rc_manage_role

    `name` = #{name, jdbcType=VARCHAR}

        AND `uid` != #{uid, jdbcType=TINYINT}

mapper.insert:
/* SystemRoleManagementMapper.insert() */
INSERT INTO rc_manage_role( `uid`
                          , `name`
                          , `description`
                          , `enabled`
                          , `created_user_uid`
                          , `created_utc_at`
                          , `updated_user_uid`
                          , `updated_utc_at`)
VALUES ( #{uid, jdbcType=TINYINT}
       , #{name, jdbcType=VARCHAR}
       , #{description, jdbcType=VARCHAR}
       , 'Y'
       , #{userUid, jdbcType=VARCHAR}
       , CURRENT_TIMESTAMP()
       , #{userUid, jdbcType=VARCHAR}
       , CURRENT_TIMESTAMP())

mapper.select:
/* SystemRoleManagementMapper.isExistByUid() */
SELECT COUNT(*) > 0
FROM rc_manage_role
WHERE `uid` = #{uid, jdbcType=TINYINT}

/* SystemRoleManagementMapper.isOwner() */
SELECT COUNT(*) > 0
FROM rc_manage_role
WHERE `uid` = #{uid, jdbcType=TINYINT}
  AND `created_user_uid` = #{userUid, jdbcType=VARCHAR}

mapper.delete:
/* SystemRoleManagementMapper.delete() */
DELETE
FROM rc_manage_role
WHERE `uid` = #{uid, jdbcType=TINYINT}

mapper.update:
/* SystemRoleManagementMapper.update() */
UPDATE rc_manage_role
SET `name`             = #{name, jdbcType=VARCHAR}
  , `description`      = #{description, jdbcType=VARCHAR}
  , `enabled`          = #{enabled, jdbcType=VARCHAR}
  , `updated_user_uid` = #{userUid, jdbcType=VARCHAR}
  , `updated_utc_at`   = CURRENT_TIMESTAMP()
WHERE `uid` = #{uid, jdbcType=TINYINT}
