mapper.select:
/* SystemRoleManagementMapper.selectList() */ SELECT `uid`, `name`, `enabled`, `created_user_uid`, `created_utc_at`, `updated_user_uid`, `updated_utc_at` FROM rc_manage_role
AND `uid` LIKE CONCAT('%', #{pagination.uid, jdbcType=VARCHAR}, '%')
AND `name` LIKE CONCAT('%', #{pagination.name}, '%')
AND `created_utc_at` >= #{pagination.startDate}
AND `created_utc_at` <= #{pagination.endDate}
ORDER BY `updated_utc_at` DESC LIMIT #{pagination.limit, jdbcType=INTEGER} OFFSET #{pagination.offset, jdbcType=INTEGER}
